import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './HomePage.css'; // Create this for custom styles

const HomePage = () => {
  const [mobiles, setMobiles] = useState([]);
  const [expandedCard, setExpandedCard] = useState(null); // âœ… Track which card is open

  const fetchMobiles = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/mobiles');
      setMobiles(res.data);
    } catch (err) {
      console.error('Failed to load catalog:', err);
    }
  };

  useEffect(() => {
    fetchMobiles();
  }, []);

  const handleInquiry = (mobile) => {
    const message = `Hello, I'm interested in this mobile:\n\nBrand: ${mobile.brand}\nModel: ${mobile.model}\nRAM: ${mobile.ram}\nStorage: ${mobile.storage}\nPrice: â‚¹${mobile.price}\nCondition: ${mobile.condition}`;
    const whatsappURL = `https://wa.me/?text=${encodeURIComponent(message)}`;
    window.open(whatsappURL, '_blank');
  };

  const toggleCard = (id) => {
    setExpandedCard((prev) => (prev === id ? null : id));
  };

  return (
    <div className="homepage">
      <h2>ðŸ“± Available Mobiles</h2>
      <div className="mobile-grid">
        {mobiles.map((mobile) => (
          <div
            key={mobile._id}
            className={`mobile-card ${expandedCard === mobile._id ? 'expanded' : ''}`}
            onClick={() => toggleCard(mobile._id)}
          >
            <img src={mobile.imageUrl} alt="mobile" />
            <h4>{mobile.brand} - {mobile.model}</h4>
            {expandedCard === mobile._id && (
              <>
                <p>RAM: {mobile.ram}</p>
                <p>Storage: {mobile.storage}</p>
                <p>Price: â‚¹{mobile.price}</p>
                <p>Condition: {mobile.condition}</p>
                <button onClick={(e) => {
                  e.stopPropagation();
                  handleInquiry(mobile);
                }}>
                  ðŸ“© WhatsApp Inquiry
                </button>
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
