import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './MobileDetailPage.css';

const MobileDetailPage = () => {
  const { id } = useParams();
  const [mobile, setMobile] = useState(null);

  useEffect(() => {
    const fetchMobile = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/mobiles/${id}`);
        setMobile(res.data);
      } catch (err) {
        console.error('Error loading mobile details:', err);
      }
    };
    fetchMobile();
  }, [id]);

  const handleInquiry = () => {
    if (!mobile) return;
    const message = `Hello, I'm interested in this mobile:\n\nBrand: ${mobile.brand}\nModel: ${mobile.model}\nRAM: ${mobile.ram}\nStorage: ${mobile.storage}\nPrice: â‚¹${mobile.price}\nCondition: ${mobile.condition}`;
    const whatsappURL = `https://wa.me/?text=${encodeURIComponent(message)}`;
    window.open(whatsappURL, '_blank');
  };

  if (!mobile) return <div>Loading...</div>;

  return (
    <div className="detail-page">
      <img src={mobile.imageUrl} alt={mobile.model} />
      <div className="details">
        <h2>{mobile.brand} - {mobile.model}</h2>
        <p><strong>RAM:</strong> {mobile.ram}</p>
        <p><strong>Storage:</strong> {mobile.storage}</p>
        <p><strong>Price:</strong> â‚¹{mobile.price}</p>
        <p><strong>Condition:</strong> {mobile.condition}</p>
        <button onClick={handleInquiry}>ðŸ“© WhatsApp Inquiry</button>
      </div>
    </div>
  );
};

export default MobileDetailPage;
