import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [formData, setFormData] = useState({
    imageUrl: '',
    brand: '',
    model: '',
    ram: '',
    storage: '',
    price: '',
    condition: '',
  });

  const [mobiles, setMobiles] = useState([]);

  const fetchMobiles = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/mobiles');
      setMobiles(res.data);
    } catch (err) {
      console.error('Failed to fetch mobiles:', err);
    }
  };

  useEffect(() => {
    fetchMobiles();
  }, []);

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('admin_token');

    try {
      const res = await axios.post(
        'http://localhost:5000/api/mobiles/upload',
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      alert('Mobile uploaded ✅');
      setFormData({
        imageUrl: '',
        brand: '',
        model: '',
        ram: '',
        storage: '',
        price: '',
        condition: '',
      });
      fetchMobiles(); // Refresh list
    } catch (err) {
      alert('Upload failed ❌');
      console.error(err);
    }
  };

  const handleDelete = async (id) => {
    const token = localStorage.getItem('admin_token');
    if (!window.confirm('Delete this mobile?')) return;

    try {
      await axios.delete(`http://localhost:5000/api/mobiles/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchMobiles();
    } catch (err) {
      console.error('Delete failed:', err);
      alert('Delete failed');
    }
  };

  return (
    <div style={{ padding: '40px' }}>
      <h2>📤 Upload New Mobile</h2>
      <form onSubmit={handleSubmit} style={{ maxWidth: '400px' }}>
        {['imageUrl', 'brand', 'model', 'ram', 'storage', 'price', 'condition'].map((field) => (
          <input
            key={field}
            type={field === 'price' ? 'number' : 'text'}
            name={field}
            placeholder={field}
            value={formData[field]}
            onChange={handleChange}
            required={['imageUrl', 'brand', 'model'].includes(field)}
            style={{ margin: '5px', width: '100%' }}
          />
        ))}
        <button type="submit">Upload</button>
      </form>

      <h2 style={{ marginTop: '40px' }}>📋 Uploaded Mobiles</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
        {mobiles.map((mobile) => (
          <div key={mobile._id} style={{ border: '1px solid #ccc', padding: '10px', width: '220px' }}>
            <img src={mobile.imageUrl} alt="mobile" style={{ width: '100%', height: 'auto' }} />
            <h4>{mobile.brand} - {mobile.model}</h4>
            <p>RAM: {mobile.ram}</p>
            <p>Storage: {mobile.storage}</p>
            <p>Price: ₹{mobile.price}</p>
            <p>Condition: {mobile.condition}</p>
            <button onClick={() => handleDelete(mobile._id)} style={{ color: 'red' }}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminDashboard;
