import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import AdminNavbar from '../components/AdminNavbar';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState('upload');
  const [mobiles, setMobiles] = useState([]);
  const [images, setImages] = useState([]);
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(false);
  const [form, setForm] = useState({
    brand: '',
    model: '',
    ram: '',
    storage: '',
    price: '',
    color: ''
  });

  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  const fetchMobiles = useCallback(async () => {
    try {
      setLoading(true);
      const { data } = await axios.get('http://localhost:5000/api/mobiles', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMobiles(data);
    } catch (error) {
      console.error('Failed to fetch mobiles:', error);
    } finally {
      setLoading(false);
    }
  }, [token]);

  useEffect(() => {
    if (!token) navigate('/login');
    else fetchMobiles();
  }, [token, navigate, fetchMobiles]);

  const handleInputChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    Object.entries(form).forEach(([key, val]) => formData.append(key, val));
    images.forEach((file) => formData.append('images', file));

    try {
      setLoading(true);
      await axios.post('http://localhost:5000/api/mobiles/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`
        }
      });

      alert('Mobile uploaded successfully!');
      resetForm();
      fetchMobiles();
    } catch (error) {
      console.error('Upload failed:', error);
      alert('Failed to upload mobile.');
    } finally {
      setLoading(false);
    }
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const formData = new FormData();
      Object.entries(form).forEach(([key, val]) => formData.append(key, val));
      images.forEach((file) => formData.append('images', file));

      await axios.put(`http://localhost:5000/api/mobiles/${editId}`, formData, {
        headers: { Authorization: `Bearer ${token}` }
      });

      alert('Mobile updated successfully!');
      resetForm();
      fetchMobiles();
    } catch (error) {
      console.error('Update failed:', error);
      alert('Failed to update mobile.');
    } finally {
      setLoading(false);
    }
  };

const handleEdit = (mobile) => {
  navigate(`/admin/edit/${mobile._id}`);
};

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this mobile?')) return;

    try {
      setLoading(true);
      await axios.delete(`http://localhost:5000/api/mobiles/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMobiles(mobiles.filter((m) => m._id !== id));
    } catch (error) {
      console.error('Delete failed:', error);
      alert('Failed to delete mobile.');
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setForm({ brand: '', model: '', ram: '', storage: '', price: '', color: '' });
    setImages([]);
    setEditId(null);
  };

  const filteredMobiles = mobiles.filter((m) =>
    `${m.brand} ${m.model}`.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="admin-dashboard">
     {loading && (
  <div className="spinner-overlay">
    <div className="elegant-spinner"></div>
    <div className="spinner-text">Please wait...</div>
  </div>
)}

      <AdminNavbar activeTab={activeTab} setActiveTab={setActiveTab} />
      <div className="admin-content">
        {activeTab === 'upload' && (
          <form className="upload-form" onSubmit={editId ? handleUpdate : handleUpload}>
            <h2>{editId ? 'Edit Mobile' : 'Upload Mobile'}</h2>
            {['brand', 'model', 'ram', 'storage', 'price', 'color'].map((field) => (
              <input
                key={field}
                name={field}
                type={field === 'price' ? 'number' : 'text'}
                placeholder={field.charAt(0).toUpperCase() + field.slice(1)}
                value={form[field]}
                onChange={handleInputChange}
                required
              />
            ))}

            <label className="upload-button">
              Select Images
              <input
                type="file"
                accept="image/*"
                multiple
                hidden
                onChange={(e) => setImages(Array.from(e.target.files))}
              />
            </label>

            {images.length > 0 && (
              <div className="preview-grid">
                {images.map((img, i) => (
                  <img
                    key={i}
                    src={URL.createObjectURL(img)}
                    alt={`Preview ${i}`}
                    className="preview-thumb"
                  />
                ))}
              </div>
            )}

            <button type="submit">{editId ? 'Update Mobile' : 'Upload Mobile'}</button>
            {editId && (
              <button type="button" className="cancel-btn" onClick={resetForm}>
                Cancel Edit
              </button>
            )}
          </form>
        )}

        {activeTab === 'manage' && (
          <div className="mobile-list">
            <h2>📱 Uploaded Mobiles</h2>
            <input
              className="search-bar"
              type="text"
              placeholder="Search by brand or model..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />

            {filteredMobiles.length === 0 ? (
              <p>No mobiles found.</p>
            ) : (
              <div className="grid-view">
                {filteredMobiles.map((mobile) => (
                  <div key={mobile._id} className="mobile-card">
                    <img src={mobile.imageUrls?.[0] || '/no-image.png'} alt={mobile.model} />
                    <div className="mobile-info">
                      <h3>{mobile.brand} - {mobile.model}</h3>
  <div className="mobile-id-badge">{mobile.mobileId}</div> 

                      <p>{mobile.ram} / {mobile.storage}</p>
                      <p>{mobile.color} — ₹{mobile.price}</p>
                      <div className="action-buttons">
<button onClick={() => handleEdit(mobile)} className="edit-btn">Edit</button>


                        <button onClick={() => handleDelete(mobile._id)} className="delete-btn">Delete</button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminDashboard;
