import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { SearchContext } from '../context/SearchContext';
import './HomePage.css';

const HomePage = () => {
  const [mobiles, setMobiles] = useState([]);
  const [filters, setFilters] = useState({
    brand: [],
    ram: [],
    storage: [],
    condition: [],
  });

  const [filteredMobiles, setFilteredMobiles] = useState([]);
  const [dropdownOpen, setDropdownOpen] = useState({
    brand: true,
    ram: false,
    storage: false,
    condition: false,
  });

  const { searchQuery } = useContext(SearchContext); // âœ… use global search term

  useEffect(() => {
    const fetchMobiles = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/mobiles');
        setMobiles(res.data);
        setFilteredMobiles(res.data);
      } catch (err) {
        console.error('Failed to load catalog:', err);
      }
    };
    fetchMobiles();
  }, []);

  const unique = (key) => [...new Set(mobiles.map((m) => m[key]))];

  const handleCheckboxChange = (type, value) => {
    setFilters((prev) => {
      const current = prev[type];
      const updated = current.includes(value)
        ? current.filter((item) => item !== value)
        : [...current, value];
      return { ...prev, [type]: updated };
    });
  };

  const toggleDropdown = (type) => {
    setDropdownOpen((prev) => ({
      ...prev,
      [type]: !prev[type],
    }));
  };

  useEffect(() => {
    let results = [...mobiles];

    // Apply filters
    Object.entries(filters).forEach(([key, values]) => {
      if (values.length > 0) {
        results = results.filter((m) => values.includes(m[key]));
      }
    });

    // Apply search from navbar
    if (searchQuery.trim()) {
      const lower = searchQuery.toLowerCase();
      results = results.filter(
        (m) =>
          m.brand.toLowerCase().includes(lower) ||
          m.model.toLowerCase().includes(lower)
      );
    }

    setFilteredMobiles(results);
  }, [filters, searchQuery, mobiles]);

  return (
    <div className="homepage-grid">
      {/* === Sidebar Filters === */}
      <aside className="filter-sidebar">
        <h3>Filter</h3>

        {['brand', 'ram', 'storage', 'condition'].map((type) => (
          <div key={type} className="filter-group">
            <div
              className="filter-header"
              onClick={() => toggleDropdown(type)}
            >
              <span>{type.charAt(0).toUpperCase() + type.slice(1)}</span>
              <span className="arrow">
                {dropdownOpen[type] ? 'â–²' : 'â–¼'}
              </span>
            </div>

            {dropdownOpen[type] && (
              <div className="filter-options">
                {unique(type).map((val) => (
                  <label key={val} className="checkbox">
                    <input
                      type="checkbox"
                      onChange={() => handleCheckboxChange(type, val)}
                      checked={filters[type].includes(val)}
                    />
                    <span>{val}</span>
                  </label>
                ))}
              </div>
            )}
          </div>
        ))}
      </aside>

      {/* === Grid Section === */}
      <main className="catalog-section">
        <h2>ðŸ“± Available Mobiles</h2>
        <div className="mobile-grid">
          {filteredMobiles.map((mobile) => (
            <Link
              to={`/mobile/${mobile._id}`}
              key={mobile._id}
              className="mobile-card-link"
            >
              <div className="mobile-card">
                <div className="image-wrapper">
                  <img src={mobile.imageUrl} alt={mobile.model} />
                </div>
                <div className="card-text">
                  <h4>
                    {mobile.brand} - {mobile.model}
                  </h4>
                  <p>Click for details</p>
                </div>
              </div>
            </Link>
          ))}
        </div>
      </main>
    </div>
  );
};

export default HomePage;
