import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './EditMobilePage.css';

const EditMobilePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [mobile, setMobile] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMobile = async () => {
      try {
        const { data } = await axios.get(`http://localhost:5000/api/mobiles/${id}`);
        setMobile(data);
      } catch (err) {
        console.error('Failed to load mobile:', err);
      } finally {
        setLoading(false);
      }
    };
    fetchMobile();
  }, [id]);

  const handleChange = (e) => {
    setMobile({ ...mobile, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5000/api/mobiles/${id}`, mobile, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      alert('Mobile updated successfully');
      navigate('/admin/manage');
    } catch (err) {
      console.error('Update error:', err);
    }
  };

  if (loading || !mobile) return <p>Loading...</p>;

  return (
    <div className="edit-mobile-container">
      <h2>‚úèÔ∏è Edit Mobile</h2>
      <form onSubmit={handleSubmit}>
        <input name="brand" value={mobile.brand} onChange={handleChange} placeholder="Brand" required />
        <input name="model" value={mobile.model} onChange={handleChange} placeholder="Model" required />
        <input name="ram" value={mobile.ram} onChange={handleChange} placeholder="RAM" />
        <input name="storage" value={mobile.storage} onChange={handleChange} placeholder="Storage" />
        <input name="condition" value={mobile.condition} onChange={handleChange} placeholder="Condition" />
        <input name="price" value={mobile.price} onChange={handleChange} placeholder="Price" />

        <button type="submit">üíæ Save Changes</button>
      </form>

      <div className="image-list">
        <h4>Uploaded Images</h4>
        {mobile.imageUrls?.map((url, index) => (
          <div key={index} className="image-item">
            <img src={url} alt="mobile" />
            {/* You can add image delete logic here if needed */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default EditMobilePage;
