import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ManageMobiles.css';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/navigation';
import { Navigation } from 'swiper/modules';
import { useNavigate } from 'react-router-dom';

const ManageMobiles = () => {
  const [mobiles, setMobiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const fetchMobiles = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/mobiles');
      setMobiles(res.data);
    } catch (error) {
      console.error('Error fetching mobiles:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    const confirm = window.confirm('Are you sure you want to delete this mobile?');
    if (!confirm) return;

    try {
      await axios.delete(`http://localhost:5000/api/mobiles/${id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      setMobiles(mobiles.filter((m) => m._id !== id));
    } catch (error) {
      console.error('Delete error:', error);
    }
  };

  const handleEdit = (id) => {
    navigate(`/admin/edit/${id}`);
  };

  useEffect(() => {
    fetchMobiles();
  }, []);

  return (
    <div className="manage-mobiles-container">
      <h2>üì± Manage Mobiles</h2>
      {loading ? (
        <p className="loading-text">Loading...</p>
      ) : mobiles.length === 0 ? (
        <p className="no-data-text">No mobiles found.</p>
      ) : (
        <div className="mobiles-grid">
          {mobiles.map((mobile) => (
            <div className="mobile-card" key={mobile._id}>
              <div className="image-slider">
                <Swiper navigation={true} modules={[Navigation]}>
                  {mobile.imageUrls?.map((img, index) => (
                    <SwiperSlide key={index}>
                      <img src={img} alt={`slide-${index}`} />
                    </SwiperSlide>
                  ))}
                </Swiper>
              </div>

              <div className="details">
                <h3>{mobile.brand} {mobile.model}</h3>
                <p><strong>RAM:</strong> {mobile.ram}</p>
                <p><strong>Storage:</strong> {mobile.storage}</p>
                <p><strong>Condition:</strong> {mobile.condition}</p>
                <p><strong>Price:</strong> ‚Çπ{mobile.price}</p>
              </div>

              <div className="actions">
                <button className="edit-btn" onClick={() => handleEdit(mobile._id)}>
                  ‚úèÔ∏è Edit
                </button>
                <button className="delete-btn" onClick={() => handleDelete(mobile._id)}>
                  üóë Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ManageMobiles;
