// AdminDashboard.js (Premium with Manage + Search)
import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import AdminNavbar from '../components/AdminNavbar';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState('upload');
  const [mobiles, setMobiles] = useState([]);
  const [image, setImage] = useState(null);
  const [search, setSearch] = useState('');
  const [form, setForm] = useState({ brand: '', model: '', ram: '', storage: '', price: '', condition: '' });

  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  const fetchMobiles = useCallback(async () => {
    try {
      const { data } = await axios.get('http://localhost:5000/api/mobiles', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMobiles(data);
    } catch (error) {
      console.error('Failed to fetch mobiles:', error);
    }
  }, [token]);

  useEffect(() => {
    if (!token) navigate('/login');
    else fetchMobiles();
  }, [token, navigate, fetchMobiles]);

  const handleInputChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    Object.entries(form).forEach(([key, val]) => formData.append(key, val));
    formData.append('image', image);
    try {
      await axios.post('http://localhost:5000/api/mobiles/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`,
        },
      });
      alert('✅ Mobile uploaded successfully!');
      setForm({ brand: '', model: '', ram: '', storage: '', price: '', condition: '' });
      setImage(null);
      fetchMobiles();
    } catch (error) {
      console.error('Upload failed:', error);
      alert('Failed to upload mobile.');
    }
  };

  // Add to useState at top
const [editId, setEditId] = useState(null);

// Handle Edit Fill
const handleEdit = (mobile) => {
  setForm({
    brand: mobile.brand,
    model: mobile.model,
    ram: mobile.ram,
    storage: mobile.storage,
    price: mobile.price,
    condition: mobile.condition,
  });
  setEditId(mobile._id);
};

// Handle Update
const handleUpdate = async (e) => {
  e.preventDefault();
  try {
    await axios.put(`http://localhost:5000/api/mobiles/${editId}`, form, {
      headers: { Authorization: `Bearer ${token}` },
    });
    alert("✅ Mobile updated successfully!");
    setForm({ brand: '', model: '', ram: '', storage: '', price: '', condition: '' });
    setEditId(null);
    fetchMobiles();
  } catch (error) {
    console.error("Update failed:", error);
    alert("Failed to update mobile.");
  }
};


  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this mobile?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/mobiles/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMobiles(mobiles.filter((m) => m._id !== id));
    } catch (error) {
      console.error('Delete failed:', error);
      alert('Failed to delete mobile.');
    }
  };

  const filteredMobiles = mobiles.filter(m =>
    m.brand.toLowerCase().includes(search.toLowerCase()) ||
    m.model.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="admin-dashboard">
      <AdminNavbar activeTab={activeTab} setActiveTab={setActiveTab} />
      <div className="admin-content">
        {activeTab === 'upload' && (
          <form className="upload-form" onSubmit={handleUpload}>
            <h2>Upload Mobile</h2>
            {['brand', 'model', 'ram', 'storage', 'price', 'condition'].map((field) => (
              <input
                key={field}
                name={field}
                type={field === 'price' ? 'number' : 'text'}
                placeholder={field.charAt(0).toUpperCase() + field.slice(1)}
                value={form[field]}
                onChange={handleInputChange}
                required
              />
            ))}
            <input type="file" accept="image/*" onChange={(e) => setImage(e.target.files[0])} required />
            <button type="submit">Upload</button>
          </form>
        )}

        {activeTab === 'manage' && (
          <div className="mobile-list">
            <h2>Uploaded Mobiles</h2>
            <div className="search-bar">
              <input
                type="text"
                placeholder="Search by brand or model"
                value={search}
                onChange={(e) => setSearch(e.target.value)}
              />
            </div>
            <div className="mobile-grid">
              {filteredMobiles.length === 0 ? (
                <p>No mobiles found.</p>
              ) : (
                filteredMobiles.map((mobile) => (
                  <div key={mobile._id} className="mobile-card">
                    <img src={mobile.imageUrl} alt={mobile.model} />
                    <h3>{mobile.brand} - {mobile.model}</h3>
                    <p>{mobile.ram} / {mobile.storage}</p>
                    <p>{mobile.condition} - ₹{mobile.price}</p>
                    <button onClick={() => handleDelete(mobile._id)}>Delete</button>
                  </div>
                ))
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
