// src/components/AdminNavbar.js
import React, { useState, useEffect, useRef } from 'react';
import './AdminNavbar.css';

const AdminNavbar = ({ activeTab, setActiveTab }) => {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const buttonRef = useRef(null);
  const [dropdownPos, setDropdownPos] = useState({ top: 60, right: 16 });

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  useEffect(() => {
    if (dropdownOpen && buttonRef.current) {
      const rect = buttonRef.current.getBoundingClientRect();
      setDropdownPos({
        top: rect.bottom + 4,
        right: window.innerWidth - rect.right,
      });
    }
  }, [dropdownOpen]);

  return (
    <nav className="admin-navbar">
      <div className="admin-navbar-content">
        <div className="admin-navbar-logo">JB Admin</div>

        <div className="admin-navbar-buttons">
          <button
            className={activeTab === 'upload' ? 'active' : ''}
            onClick={() => setActiveTab('upload')}
          >
            Upload
          </button>
          <button
            className={activeTab === 'manage' ? 'active' : ''}
            onClick={() => setActiveTab('manage')}
          >
            Manage
          </button>
        </div>

        <div className="menu-toggle-wrapper" ref={buttonRef}>
          <div
            className="menu-toggle"
            onClick={() => setDropdownOpen(!dropdownOpen)}
            aria-label="Toggle Logout"
          >
            <div className="bar"></div>
            <div className="bar"></div>
            <div className="bar"></div>
          </div>
        </div>
      </div>

      {dropdownOpen && (
        <div
          className="dropdown-menu logout-only fixed-dropdown"
          style={{
            top: `${dropdownPos.top}px`,
            right: `${dropdownPos.right}px`,
          }}
        >
          <button onClick={handleLogout}>Logout</button>
        </div>
      )}
    </nav>
  );
};

export default AdminNavbar;
