// src/pages/MobileDetailPage.js
import React, { useEffect, useState, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './MobileDetailPage.css';
import { CartContext } from '../context/CartContext';

import { Swiper, SwiperSlide } from 'swiper/react';
import { Pagination } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/pagination';

const MobileDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useContext(CartContext);

  const [mobile, setMobile] = useState(null);
  const [suggestions, setSuggestions] = useState([]);

  useEffect(() => {
    const fetchMobile = async () => {
      try {
        const { data } = await axios.get(`http://localhost:5000/api/mobiles`);
        const current = data.find((m) => m._id === id);
        setMobile(current);
        setSuggestions(data.filter((m) => m._id !== id).slice(0, 4));
      } catch (error) {
        console.error('Failed to load mobile details:', error);
      }
    };

    fetchMobile();
  }, [id]);

  const handleAddToCart = () => {
    addToCart(mobile);
    alert("Mobile added to cart!");
  };

  if (!mobile) return <div className="loading-text">Loading...</div>;

  return (
    <div className="mobile-detail-container">
      <div className="mobile-detail-card">
        {/* ✅ Swiper Slider for imageUrls */}
        <Swiper
          spaceBetween={10}
          slidesPerView={1}
          pagination={{ clickable: true }}
          modules={[Pagination]}
          className="mobile-slider"
        >
          {mobile.imageUrls?.map((url, index) => (
            <SwiperSlide key={index}>
              <img src={url} alt={`Slide ${index}`} className="main-image" />
            </SwiperSlide>
          ))}
        </Swiper>

        <h2>{mobile.brand} {mobile.model}</h2>
        <p className="price">₹{mobile.price}</p>
        <p><strong>RAM:</strong> {mobile.ram}</p>
        <p><strong>Storage:</strong> {mobile.storage}</p>
        <p><strong>Color:</strong> {mobile.color}</p>
        <button onClick={handleAddToCart}>Add to Enquiry Cart</button>
      </div>

      <div className="suggestion-panel">
        <h3>Similar Mobiles</h3>
        {suggestions.map((item) => (
          <div
            className="suggestion-card"
            key={item._id}
            onClick={() => navigate(`/mobile/${item._id}`)}
          >
            <img src={item.imageUrls?.[0]} alt={item.model} />
            <div>
              <p>{item.brand} {item.model}</p>
              <span>₹{item.price}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MobileDetailPage;
