import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';

const Navbar = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const isAdminPage = location.pathname.startsWith('/admin');

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  return (
    <div style={{
      backgroundColor: '#f8f8f8',
      padding: '10px 20px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      borderBottom: '1px solid #ddd'
    }}>
      <h3>
        <Link to="/" style={{ textDecoration: 'none', color: '#333' }}>JollyBaba ðŸ“±</Link>
      </h3>

      <div style={{ display: 'flex', gap: '12px', alignItems: 'center' }}>
        {isAdminPage && token && (
          <>
            <Link to="/admin">
              <button style={{ padding: '6px 12px' }}>Upload</button>
            </Link>
            <Link to="/admin/manage">
              <button style={{ padding: '6px 12px' }}>Manage</button>
            </Link>
            <button onClick={handleLogout} style={{ padding: '6px 12px', background: 'red', color: '#fff' }}>
              Logout
            </button>
          </>
        )}

        {!isAdminPage && (
          <Link to="/login">
            <button style={{ padding: '6px 12px' }}>Login as Admin</button>
          </Link>
        )}
      </div>
    </div>
  );
};

export default Navbar;
