// src/pages/EditMobilePage.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './EditMobilePage.css';

const EditMobilePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    brand: '',
    model: '',
    ram: '',
    storage: '',
    condition: '',
    price: '',
    imageUrls: [],
  });

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMobile = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/mobiles/${id}`);
        setForm(res.data);
      } catch (error) {
        console.error('Error fetching mobile data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchMobile();
  }, [id]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5000/api/mobiles/${id}`, form, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      alert('Mobile updated successfully');
      navigate('/admin/manage');
    } catch (error) {
      console.error('Update error:', error);
      alert('Update failed');
    }
  };

  if (loading) return <p className="loading-text">Loading...</p>;

  return (
    <div className="edit-mobile-container">
      <h2>‚úèÔ∏è Edit Mobile</h2>
      <form className="edit-form" onSubmit={handleSubmit}>
        {['brand', 'model', 'ram', 'storage', 'condition', 'price'].map((field) => (
          <div className="form-group" key={field}>
            <label>{field.charAt(0).toUpperCase() + field.slice(1)}</label>
            <input
              type="text"
              name={field}
              value={form[field] || ''}
              onChange={handleChange}
              required
            />
          </div>
        ))}

        <div className="form-group">
          <label>Image URLs (comma separated)</label>
          <textarea
            name="imageUrls"
            rows="3"
            value={form.imageUrls.join(',')}
            onChange={(e) => setForm({ ...form, imageUrls: e.target.value.split(',') })}
          ></textarea>
        </div>

        <button type="submit" className="save-btn">üíæ Save Changes</button>
      </form>
    </div>
  );
};

export default EditMobilePage;
