import React, { useEffect, useState } from 'react';
import axios from 'axios';
import CartDrawer from '../components/CartDrawer';

const HomePage = () => {
  const [mobiles, setMobiles] = useState([]);
  const [cartItems, setCartItems] = useState([]);
  const [isCartOpen, setIsCartOpen] = useState(false);

  const fetchMobiles = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/mobiles');
      setMobiles(res.data);
    } catch (err) {
      console.error('Failed to load catalog:', err);
    }
  };

  useEffect(() => {
    fetchMobiles();
  }, []);

  const handleInquiry = (items) => {
    const message = items.map((m, i) =>
      `Mobile ${i + 1}:\nBrand: ${m.brand}\nModel: ${m.model}\nRAM: ${m.ram}\nStorage: ${m.storage}\nPrice: â‚¹${m.price}\nCondition: ${m.condition}`
    ).join('\n\n');

    const whatsappURL = `https://wa.me/?text=${encodeURIComponent("Hello, I'm interested in the following mobiles:\n\n" + message)}`;
    window.open(whatsappURL, '_blank');
  };

  const addToCart = (mobile) => {
    if (!cartItems.find(item => item._id === mobile._id)) {
      setCartItems([...cartItems, mobile]);
    }
    setIsCartOpen(true);
  };

  return (
    <div style={{ padding: '40px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h2>ðŸ“± Available Mobiles</h2>
        <button onClick={() => setIsCartOpen(true)}>ðŸ›’ View Cart ({cartItems.length})</button>
      </div>

      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
        {mobiles.map((mobile) => (
          <div key={mobile._id} style={{ border: '1px solid #ccc', padding: '10px', width: '220px' }}>
            <img src={mobile.imageUrl} alt="mobile" style={{ width: '100%', height: 'auto' }} />
            <h4>{mobile.brand} - {mobile.model}</h4>
            <p>RAM: {mobile.ram}</p>
            <p>Storage: {mobile.storage}</p>
            <p>Price: â‚¹{mobile.price}</p>
            <p>Condition: {mobile.condition}</p>
            <button onClick={() => addToCart(mobile)}>âž• Add to Cart</button>
          </div>
        ))}
      </div>

      <CartDrawer
        isOpen={isCartOpen}
        onClose={() => setIsCartOpen(false)}
        cartItems={cartItems}
        onInquiry={handleInquiry}
      />
    </div>
  );
};

export default HomePage;
