// frontend/pages/EditMobilePage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import './EditMobilePage.css';

const EditMobilePage = () => {
  const [form, setForm] = useState({ brand: '', model: '', ram: '', storage: '', price: '', color: '' });
  const [existingImages, setExistingImages] = useState([]);
  const [newImages, setNewImages] = useState([]);
  const [imagesToDelete, setImagesToDelete] = useState([]);
  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    const fetchMobile = async () => {
      const res = await axios.get(`http://localhost:5000/api/mobiles/${id}`);
      const data = res.data;
      setForm({
        brand: data.brand,
        model: data.model,
        ram: data.ram,
        storage: data.storage,
        price: data.price,
        color: data.color
      });
      setExistingImages(data.imageUrls.map((url, idx) => ({ url, public_id: data.imagePublicIds[idx] })));
    };
    fetchMobile();
  }, [id]);

  const handleInputChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleImageDelete = (public_id) => {
    setImagesToDelete([...imagesToDelete, public_id]);
    setExistingImages(existingImages.filter(img => img.public_id !== public_id));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    Object.entries(form).forEach(([key, val]) => formData.append(key, val));
    newImages.forEach((img) => formData.append('images', img));
    formData.append('imagesToDelete', JSON.stringify(imagesToDelete));

    try {
      await axios.put(`http://localhost:5000/api/mobiles/${id}`, formData, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      alert('Mobile updated!');
      navigate('/admin/manage');
    } catch (err) {
      console.error(err);
      alert('Failed to update.');
    }
  };

  return (
    <div className="edit-page">
      <h2>Edit Mobile</h2>
      <form className="edit-form" onSubmit={handleSubmit}>
        {['brand', 'model', 'ram', 'storage', 'price', 'color'].map((f) => (
          <input
            key={f}
            name={f}
            type={f === 'price' ? 'number' : 'text'}
            value={form[f]}
            onChange={handleInputChange}
            placeholder={f.charAt(0).toUpperCase() + f.slice(1)}
            required
          />
        ))}

        <label className="upload-button">
          Upload New Images
          <input type="file" multiple hidden onChange={(e) => setNewImages(Array.from(e.target.files))} />
        </label>

        <div className="existing-images">
          {existingImages.map((img, idx) => (
            <div key={idx} className="img-preview">
              <img src={img.url} alt="mobile" />
              <button type="button" onClick={() => handleImageDelete(img.public_id)}>‚ùå</button>
            </div>
          ))}
        </div>

        <button type="submit">Update</button>
      </form>
    </div>
  );
};

export default EditMobilePage;
