import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './MobileDetailPage.css';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';

const MobileDetailPage = () => {
  const { id } = useParams();
  const [mobile, setMobile] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMobile = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/mobiles/${id}`);
        setMobile(res.data);
      } catch (err) {
        console.error('Failed to fetch mobile:', err);
        navigate('/');
      } finally {
        setLoading(false);
      }
    };

    fetchMobile();
  }, [id, navigate]);

  if (loading) return <div className="detail-loading">Loading...</div>;
  if (!mobile) return <div className="detail-error">Mobile not found.</div>;

  return (
    <div className="mobile-detail-page">
      <div className="detail-container">
        <div className="detail-gallery">
          <Swiper
            navigation
            pagination={{ clickable: true }}
            modules={[Navigation, Pagination]}
            className="swiper-container"
          >
            {mobile.images?.map((img, index) => (
              <SwiperSlide key={index}>
                <img src={img} alt={`slide-${index}`} />
              </SwiperSlide>
            ))}
          </Swiper>
        </div>

        <div className="detail-info">
          <h2>{mobile.brand} {mobile.model}</h2>
          <p><strong>RAM:</strong> {mobile.ram}</p>
          <p><strong>Storage:</strong> {mobile.storage}</p>
          <p><strong>Condition:</strong> {mobile.condition}</p>
          <p><strong>Color:</strong> {mobile.color}</p>
          <p><strong>Price:</strong> â‚¹{mobile.price}</p>
        </div>
      </div>
    </div>
  );
};

export default MobileDetailPage;
