import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './HomePage.css';

const HomePage = () => {
  const [mobiles, setMobiles] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [brands, setBrands] = useState([]);
  const [selectedBrand, setSelectedBrand] = useState('All');

  useEffect(() => {
    const fetchMobiles = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/mobiles');
        const allMobiles = res.data;
        setMobiles(allMobiles);
        setFiltered(allMobiles);

        const uniqueBrands = ['All', ...new Set(allMobiles.map((m) => m.brand))];
        setBrands(uniqueBrands);
      } catch (err) {
        console.error('Failed to load catalog:', err);
      }
    };
    fetchMobiles();
  }, []);

  const handleFilter = (brand) => {
    setSelectedBrand(brand);
    if (brand === 'All') {
      setFiltered(mobiles);
    } else {
      setFiltered(mobiles.filter((m) => m.brand === brand));
    }
  };

  return (
    <div className="homepage">
      <h2>üì± Available Mobiles</h2>

      {/* üîç Filter Buttons */}
      <div className="filter-buttons">
        {brands.map((brand) => (
          <button
            key={brand}
            className={`filter-btn ${selectedBrand === brand ? 'active' : ''}`}
            onClick={() => handleFilter(brand)}
          >
            {brand}
          </button>
        ))}
      </div>

      {/* üì¶ Mobile Cards */}
      <div className="mobile-grid">
        {filtered.map((mobile) => (
          <Link to={`/mobile/${mobile._id}`} key={mobile._id} className="mobile-card-link">
            <div className="mobile-card">
              <div className="image-wrapper">
                <img src={mobile.imageUrl} alt={mobile.model} />
              </div>
              <div className="card-text">
                <h4>{mobile.brand} - {mobile.model}</h4>
                <p>Click for details</p>
              </div>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
