import React, { useContext } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { CartContext } from '../context/CartContext';

const Navbar = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const isAdminPage = location.pathname.startsWith('/admin');
  const { cart } = useContext(CartContext);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  return (
    <div style={{
      backgroundColor: '#f8f8f8',
      padding: '10px 20px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      borderBottom: '1px solid #ddd'
    }}>
      <h3>
        <Link to="/" style={{ textDecoration: 'none', color: '#333' }}>JollyBaba ðŸ“±</Link>
      </h3>

      <div style={{ display: 'flex', gap: '12px', alignItems: 'center' }}>
        {isAdminPage && token ? (
          <>
            <Link to="/admin">
              <button style={{ padding: '6px 12px' }}>Upload</button>
            </Link>
            <Link to="/admin/manage">
              <button style={{ padding: '6px 12px' }}>Manage</button>
            </Link>
            <button onClick={handleLogout} style={{ padding: '6px 12px', background: 'red', color: '#fff' }}>
              Logout
            </button>
          </>
        ) : (
          <>
            <Link to="/cart" style={{ position: 'relative' }}>
              <button style={{ padding: '6px 12px' }}>
                ðŸ›’ Cart
                {cart.length > 0 && (
                  <span style={{
                    position: 'absolute',
                    top: '-6px',
                    right: '-10px',
                    background: '#e53935',
                    color: 'white',
                    borderRadius: '50%',
                    padding: '2px 6px',
                    fontSize: '11px',
                    fontWeight: 'bold'
                  }}>
                    {cart.length}
                  </span>
                )}
              </button>
            </Link>
            <Link to="/login">
              <button style={{ padding: '6px 12px' }}>Login as Admin</button>
            </Link>
          </>
        )}
      </div>
    </div>
  );
};

export default Navbar;
