// src/pages/MobileDetailPage.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './MobileDetailPage.css';

const MobileDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [mobile, setMobile] = useState(null);
  const [suggestions, setSuggestions] = useState([]);

  useEffect(() => {
    const fetchMobile = async () => {
      try {
        const { data } = await axios.get(`http://localhost:5000/api/mobiles`);
        const current = data.find((m) => m._id === id);
        setMobile(current);
        setSuggestions(data.filter((m) => m._id !== id).slice(0, 5)); // show 5 other items
      } catch (error) {
        console.error('❌ Failed to load mobile details:', error);
      }
    };

    fetchMobile();
  }, [id]);

  const handleWhatsApp = () => {
    const message = `Hello, I'm interested in this mobile:\n\nBrand: ${mobile.brand}\nModel: ${mobile.model}\nRAM: ${mobile.ram}\nStorage: ${mobile.storage}\nPrice: ₹${mobile.price}\nCondition: ${mobile.condition}`;
    const url = `https://wa.me/?text=${encodeURIComponent(message)}`;
    window.open(url, '_blank');
  };

  if (!mobile) return <div style={{ padding: '2rem' }}>Loading...</div>;

  return (
    <div className="mobile-detail-container">
      <div className="mobile-detail-card">
        <img src={mobile.imageUrl} alt={mobile.model} />
        <div className="info">
          <h2>{mobile.brand} - {mobile.model}</h2>
          <p><strong>RAM:</strong> {mobile.ram}</p>
          <p><strong>Storage:</strong> {mobile.storage}</p>
          <p><strong>Condition:</strong> {mobile.condition}</p>
          <p className="price">₹{mobile.price}</p>
          <button onClick={handleWhatsApp}>📩 Inquire via WhatsApp</button>
        </div>
      </div>

      <div className="suggestion-panel">
        <h3>Similar Mobiles</h3>
        {suggestions.map((item) => (
          <div
            className="suggestion-card"
            key={item._id}
            onClick={() => navigate(`/mobile/${item._id}`)}
          >
            <img src={item.imageUrl} alt={item.model} />
            <div>
              <p>{item.brand} {item.model}</p>
              <span>₹{item.price}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MobileDetailPage;
