import React, { useContext } from 'react';
import { CartContext } from '../context/CartContext';
import './CartPage.css';

const CartPage = () => {
  const { cart, removeFromCart } = useContext(CartContext);

  const handleEnquiry = () => {
  if (cart.length === 0) {
    alert("üõí Your cart is empty.");
    return;
  }

  const message = `Hello, I‚Äôm interested in the following mobiles:\n\n${cart
    .map(
      (item, index) =>
        `${index + 1}. ${item.brand} ${item.model} (${item.ram}/${item.storage}) ‚Äì ‚Çπ${item.price}`
    )
    .join('\n')}`;

  const phoneNumber = '917020708747'; // ‚Üê Replace with your number
  const url = `https://wa.me/${phoneNumber}?text=${encodeURIComponent(message)}`;
  window.open(url, '_blank');
};


  return (
    <div className="cart-page">
      <h2>üì¶ Your Enquiry Cart ({cart.length}/20)</h2>

      {cart.length === 0 ? (
        <p className="empty-cart">Your cart is empty. Go add some mobiles! üòÑ</p>
      ) : (
        <div className="cart-items">
          {cart.map((item, index) => (
            <div className="cart-item" key={item._id}>
              <div className="item-number">{index + 1}.</div>
              <img src={item.imageUrl} alt={item.model} />
              <div className="info">
                <h4>{item.brand} {item.model}</h4>
                <p>{item.ram} / {item.storage}</p>
                <p>‚Çπ{item.price}</p>
              </div>
              <button onClick={() => removeFromCart(item._id)}>‚ùå</button>
            </div>
          ))}
        </div>
      )}

      {cart.length > 0 && (
        <button className="enquiry-button" onClick={handleEnquiry}>
          üì© Send Enquiry via WhatsApp
        </button>
      )}
    </div>
  );
};

export default CartPage;
