import React, { useState } from 'react';
import axios from 'axios';
import './HomePage.css'; // for styling filters and layout

const HomePage = () => {
  const [mobiles, setMobiles] = useState([]);
  const [filters, setFilters] = useState({
    brand: '',
    ram: '',
    storage: '',
  });

  const handleChange = (e) => {
    setFilters({ ...filters, [e.target.name]: e.target.value });
  };

  const handleSearch = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/mobiles');
      const filtered = res.data.filter((m) => {
        return (
          (!filters.brand || m.brand === filters.brand) &&
          (!filters.ram || m.ram === filters.ram) &&
          (!filters.storage || m.storage === filters.storage)
        );
      });
      setMobiles(filtered);
    } catch (err) {
      console.error('❌ Failed to fetch filtered data:', err);
    }
  };

  return (
    <div className="home-container">
      {/* 🔍 Filter Panel */}
      <div className="filter-panel">
        <select name="brand" value={filters.brand} onChange={handleChange}>
          <option value="">Brand</option>
          <option value="Iphone">Iphone</option>
          <option value="Samsung">Samsung</option>
          <option value="Vivo">Vivo</option>
        </select>

        <select name="ram" value={filters.ram} onChange={handleChange}>
          <option value="">RAM</option>
          <option value="4 GB">4 GB</option>
          <option value="6 GB">6 GB</option>
          <option value="8 GB">8 GB</option>
        </select>

        <select name="storage" value={filters.storage} onChange={handleChange}>
          <option value="">Storage</option>
          <option value="64 GB">64 GB</option>
          <option value="128 GB">128 GB</option>
          <option value="256 GB">256 GB</option>
        </select>

        <button onClick={handleSearch}>🔍 Search</button>
      </div>

      {/* 📱 Mobile Results */}
      <div className="mobile-list">
        {mobiles.length === 0 ? (
          <p>No results. Try filtering and clicking Search.</p>
        ) : (
          mobiles.map((mobile) => (
            <div key={mobile._id} className="mobile-card">
              <img src={mobile.imageUrl} alt={mobile.model} />
              <h4>{mobile.brand} - {mobile.model}</h4>
              <p>{mobile.ram} / {mobile.storage}</p>
              <p>₹{mobile.price}</p>
              <button
                onClick={() => {
                  const msg = `Interested in: ${mobile.brand} ${mobile.model}`;
                  const url = `https://wa.me/?text=${encodeURIComponent(msg)}`;
                  window.open(url, '_blank');
                }}
              >
                📩 WhatsApp
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default HomePage;
