import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './HomePage.css';

const HomePage = () => {
  const [mobiles, setMobiles] = useState([]);
  const [filters, setFilters] = useState({
    brand: [],
    ram: [],
    storage: [],
    condition: [],
  });

  const [filteredMobiles, setFilteredMobiles] = useState([]);

  useEffect(() => {
    const fetchMobiles = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/mobiles');
        setMobiles(res.data);
        setFilteredMobiles(res.data);
      } catch (err) {
        console.error('Failed to load catalog:', err);
      }
    };
    fetchMobiles();
  }, []);

  const unique = (key) => [...new Set(mobiles.map((m) => m[key]))];

  const handleCheckboxChange = (type, value) => {
    setFilters((prev) => {
      const current = prev[type];
      const updated = current.includes(value)
        ? current.filter((item) => item !== value)
        : [...current, value];
      return { ...prev, [type]: updated };
    });
  };

  useEffect(() => {
    const applyFilters = () => {
      let results = [...mobiles];

      Object.entries(filters).forEach(([key, values]) => {
        if (values.length > 0) {
          results = results.filter((m) => values.includes(m[key]));
        }
      });

      setFilteredMobiles(results);
    };

    applyFilters();
  }, [filters, mobiles]);

  return (
    <div className="homepage-grid">
      {/* === Filter Sidebar === */}
      <aside className="filter-sidebar">
        <h3>Filter</h3>

        <div className="filter-group">
          <label>Brand</label>
          {unique('brand').map((b) => (
            <div key={b} className="checkbox">
              <input
                type="checkbox"
                onChange={() => handleCheckboxChange('brand', b)}
                checked={filters.brand.includes(b)}
              />
              <span>{b}</span>
            </div>
          ))}
        </div>

        <div className="filter-group">
          <label>RAM</label>
          {unique('ram').map((r) => (
            <div key={r} className="checkbox">
              <input
                type="checkbox"
                onChange={() => handleCheckboxChange('ram', r)}
                checked={filters.ram.includes(r)}
              />
              <span>{r}</span>
            </div>
          ))}
        </div>

        <div className="filter-group">
          <label>Storage</label>
          {unique('storage').map((s) => (
            <div key={s} className="checkbox">
              <input
                type="checkbox"
                onChange={() => handleCheckboxChange('storage', s)}
                checked={filters.storage.includes(s)}
              />
              <span>{s}</span>
            </div>
          ))}
        </div>

        <div className="filter-group">
          <label>Condition</label>
          {unique('condition').map((c) => (
            <div key={c} className="checkbox">
              <input
                type="checkbox"
                onChange={() => handleCheckboxChange('condition', c)}
                checked={filters.condition.includes(c)}
              />
              <span>{c}</span>
            </div>
          ))}
        </div>
      </aside>

      {/* === Mobile Catalog Grid === */}
      <main className="catalog-section">
        <h2>ðŸ“± Available Mobiles</h2>
        <div className="mobile-grid">
          {filteredMobiles.map((mobile) => (
            <Link to={`/mobile/${mobile._id}`} key={mobile._id} className="mobile-card-link">
              <div className="mobile-card">
                <div className="image-wrapper">
                  <img src={mobile.imageUrl} alt={mobile.model} />
                </div>
                <div className="card-text">
                  <h4>{mobile.brand} - {mobile.model}</h4>
                  <p>Click for details</p>
                </div>
              </div>
            </Link>
          ))}
        </div>
      </main>
    </div>
  );
};

export default HomePage;
