import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './MobileDetailPage.css';

const MobileDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [mobile, setMobile] = useState(null);

  useEffect(() => {
    const fetchMobile = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/mobiles`);
        const found = res.data.find((item) => item._id === id);
        if (!found) {
          navigate('/');
        } else {
          setMobile(found);
        }
      } catch (err) {
        console.error('Failed to load mobile:', err);
        navigate('/');
      }
    };

    fetchMobile();
  }, [id, navigate]);

  if (!mobile) return <div className="mobile-detail">Loading...</div>;

  return (
    <div className="mobile-detail">
      <div className="mobile-detail-card">
        <img src={mobile.imageUrl} alt="mobile" />
        <div className="info">
          <h2>{mobile.brand} - {mobile.model}</h2>
          <p><strong>RAM:</strong> {mobile.ram}</p>
          <p><strong>Storage:</strong> {mobile.storage}</p>
          <p><strong>Condition:</strong> {mobile.condition}</p>
          <p><strong>Price:</strong> â‚¹{mobile.price}</p>
          <button
            onClick={() => {
              const message = `Hello, I'm interested in this mobile:\n\nBrand: ${mobile.brand}\nModel: ${mobile.model}\nRAM: ${mobile.ram}\nStorage: ${mobile.storage}\nPrice: â‚¹${mobile.price}\nCondition: ${mobile.condition}`;
              const whatsappURL = `https://wa.me/?text=${encodeURIComponent(message)}`;
              window.open(whatsappURL, '_blank');
            }}
          >
            ðŸ“© WhatsApp Inquiry
          </button>
        </div>
      </div>
    </div>
  );
};

export default MobileDetailPage;
