import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './EditMobilePage.css';

const EditMobilePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [form, setForm] = useState({
    brand: '',
    model: '',
    ram: '',
    storage: '',
    condition: '',
    price: '',
    imageUrls: [],
  });
  const [newImages, setNewImages] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMobile = async () => {
      try {
        const { data } = await axios.get(`http://localhost:5000/api/mobiles/${id}`);
        setMobile(data);
        setForm(data);
      } catch (err) {
        console.error('Failed to fetch mobile:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchMobile();
  }, [id]);

  const handleInputChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleDeleteImage = (index) => {
    const updated = [...form.imageUrls];
    updated.splice(index, 1);
    setForm({ ...form, imageUrls: updated });
  };

  const handleImageUpload = (e) => {
    setNewImages([...e.target.files]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // First upload new images if any
      let uploadedUrls = [];
      if (newImages.length > 0) {
        const formData = new FormData();
        newImages.forEach((img) => formData.append('images', img));
        const res = await axios.post('http://localhost:5000/api/mobiles/upload-images', formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });
        uploadedUrls = res.data.imageUrls;
      }

      const updatedMobile = {
        ...form,
        imageUrls: [...form.imageUrls, ...uploadedUrls],
      };

      await axios.put(`http://localhost:5000/api/mobiles/${id}`, updatedMobile);
      alert('Mobile updated successfully!');
      navigate('/admin/manage');
    } catch (err) {
      console.error('Update failed:', err);
      alert('Failed to update mobile');
    }
  };

  if (loading) return <div className="edit-loading">Loading...</div>;

  return (
    <div className="edit-page-wrapper">
      <h2>Edit Mobile Details</h2>
      <form className="edit-form" onSubmit={handleSubmit}>
        <div className="edit-grid">
          {['brand', 'model', 'ram', 'storage', 'condition', 'price'].map((field) => (
            <input
              key={field}
              type="text"
              name={field}
              value={form[field]}
              onChange={handleInputChange}
              placeholder={`Enter ${field}`}
              required={field !== 'condition'}
            />
          ))}
        </div>

        <div className="existing-images">
          <h4>Current Images</h4>
          <div className="image-list">
            {form.imageUrls.map((url, idx) => (
              <div key={idx} className="image-box">
                <img src={url} alt={`img-${idx}`} />
                <button type="button" onClick={() => handleDeleteImage(idx)}>Remove</button>
              </div>
            ))}
          </div>
        </div>

        <div className="new-upload">
          <label>Upload New Images:</label>
          <input type="file" multiple onChange={handleImageUpload} />
        </div>

        <button type="submit" className="submit-btn">Save Changes</button>
      </form>
    </div>
  );
};

export default EditMobilePage;
