import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import HomePage from './pages/HomePage';
import AdminDashboard from './pages/AdminDashboard';
import LoginPage from './pages/LoginPage';
import MobileDetailPage from './pages/MobileDetailPage';
import CartPage from './pages/CartPage';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import { SearchProvider } from './context/SearchContext';
import './index.css'; // Make sure global styles are imported

const AppWrapper = () => {
  const location = useLocation();
  const isAdminRoute = location.pathname.startsWith('/admin');

  return (
    <div className="app-wrapper"> {/* ✅ Flex container for sticky layout */}
      {!isAdminRoute && <Navbar />}

      <div className="app-content"> {/* ✅ Grows to push footer */}
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/cart" element={<CartPage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/admin" element={<AdminDashboard />} />
          <Route path="/admin/manage" element={<AdminDashboard />} />
          <Route path="/mobile/:id" element={<MobileDetailPage />} />
        </Routes>
      </div>

      {!isAdminRoute && <Footer />} {/* ✅ Only show on non-admin */}
    </div>
  );
};

function App() {
  return (
    <SearchProvider>
      <Router>
        <AppWrapper />
      </Router>
    </SearchProvider>
  );
}

export default App;
