import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './MobileDetailPage.css';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';

const MobileDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [mobile, setMobile] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMobile = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/mobiles/${id}`);
        setMobile(res.data);
      } catch (err) {
        console.error('Failed to fetch mobile:', err);
        navigate('/'); // Redirect to home if not found
      } finally {
        setLoading(false);
      }
    };
    fetchMobile();
  }, [id, navigate]);

  if (loading) return <div className="detail-loader">Loading...</div>;
  if (!mobile) return null;

  const handleWhatsApp = () => {
    const message = `Hello, I'm interested in this mobile:\n\n${mobile.brand} ${mobile.model}\nRAM: ${mobile.ram}, Storage: ${mobile.storage}\nPrice: â‚¹${mobile.price}`;
    const phone = '917020708747'; // Replace with your number
    const url = `https://wa.me/${phone}?text=${encodeURIComponent(message)}`;
    window.open(url, '_blank');
  };

  return (
    <div className="mobile-detail-container">
      <div className="detail-content">
        <div className="detail-gallery">
          <Swiper navigation={true} modules={[Navigation]}>
            {mobile.imageUrls?.map((img, index) => (
              <SwiperSlide key={index}>
                <img src={img} alt={`Mobile ${index}`} />
              </SwiperSlide>
            ))}
          </Swiper>
        </div>

        <div className="detail-info">
          <h2>{mobile.brand} {mobile.model}</h2>
          <p><strong>RAM:</strong> {mobile.ram}</p>
          <p><strong>Storage:</strong> {mobile.storage}</p>
          <p><strong>Condition:</strong> {mobile.condition}</p>
          <p><strong>Color:</strong> {mobile.color}</p>
          <p className="price">â‚¹{mobile.price}</p>

          <button onClick={handleWhatsApp} className="whatsapp-btn">
            ðŸ“© Enquire via WhatsApp
          </button>
        </div>
      </div>
    </div>
  );
};

export default MobileDetailPage;
