import React, { useContext } from 'react';
import { CartContext } from '../context/CartContext';
import { Link } from 'react-router-dom';
import './CartPage.css';

const CartPage = () => {
  const { cart, removeFromCart } = useContext(CartContext);

  const handleEnquiry = () => {
    if (cart.length === 0) {
      alert("ðŸ›’ Your cart is empty.");
      return;
    }

    const baseUrl = 'http://localhost:3000'; // ðŸ” Replace for production
    const message = `Hello, Iâ€™m interested in the following mobiles:\n\n${cart
      .map(
        (item, index) =>
          `${index + 1}. ${item.brand} ${item.model} (${item.ram}/${item.storage}) â€“ â‚¹${item.price}\n${baseUrl}/mobile/${item._id}`
      )
      .join('\n\n')}`;

    const phoneNumber = '917020708747';
    const url = `https://wa.me/${phoneNumber}?text=${encodeURIComponent(message)}`;
    window.open(url, '_blank');
  };

  return (
    <div className="cart-page">
      <h2>ðŸ“¦ Your Enquiry Cart <span>({cart.length}/20)</span></h2>

      {cart.length === 0 ? (
        <p className="empty-cart">Your cart is empty. Go add some mobiles! ðŸ˜„</p>
      ) : (
        <div className="cart-items">
          {cart.map((item, index) => (
            <div className="cart-item" key={item._id}>
              <div className="item-number">{index + 1}.</div>

              <Link to={`/mobile/${item._id}`} className="cart-link">
                <img src={item.imageUrls?.[0] || '/no-image.png'} alt={item.model} />
                <div className="info">
                  <h4>{item.brand} {item.model}</h4>
                  <p>{item.ram} / {item.storage}</p>
                  <p>â‚¹{item.price}</p>
                </div>
              </Link>

              <button onClick={() => removeFromCart(item._id)} className="remove-btn">
                ðŸ—‘
              </button>
            </div>
          ))}
        </div>
      )}

      {cart.length > 0 && (
        <button className="enquiry-button" onClick={handleEnquiry}>
          ðŸ“© Send Enquiry via WhatsApp
        </button>
      )}
    </div>
  );
};

export default CartPage;
