// src/pages/ManageMobiles.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './AdminDashboard.css'; // Reuse same styles
import { useNavigate } from 'react-router-dom';

const ManageMobiles = () => {
  const [mobiles, setMobiles] = useState([]);
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) {
      navigate('/login');
      return;
    }

    const fetchMobiles = async () => {
      try {
        const response = await axios.get('/api/mobiles', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setMobiles(response.data);
      } catch (err) {
        console.error('Error fetching mobiles:', err);
      }
    };

    fetchMobiles();
  }, [token, navigate]);

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this mobile?')) return;

    try {
      await axios.delete(`/api/mobiles/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMobiles(mobiles.filter((m) => m._id !== id));
    } catch (err) {
      console.error('Delete failed:', err);
      alert('Failed to delete mobile');
    }
  };

  return (
    <div className="admin-dashboard">
      <h1>Manage Uploaded Mobiles</h1>

      <div className="mobile-list">
        {mobiles.map((mobile) => (
          <div key={mobile._id} className="mobile-card">
            <img src={mobile.imageUrl} alt={mobile.model} />
            <div>
              <h3>{mobile.brand} - {mobile.model}</h3>
              <p>{mobile.ram} / {mobile.storage}</p>
              <p>{mobile.condition} - â‚¹{mobile.price}</p>
              <button onClick={() => handleDelete(mobile._id)}>Delete</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ManageMobiles;
