import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './HomePage.css';

const HomePage = () => {
  const [mobiles, setMobiles] = useState([]);
  const [view, setView] = useState('grid'); // 'grid' or 'list'

  useEffect(() => {
    const fetchMobiles = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/mobiles');
        setMobiles(res.data);
      } catch (err) {
        console.error('Failed to load catalog:', err);
      }
    };
    fetchMobiles();
  }, []);

  const toggleView = () => {
    setView(prev => (prev === 'grid' ? 'list' : 'grid'));
  };

  return (
    <div className="homepage">
      <div className="homepage-header">
        <h2>ðŸ“± Available Mobiles</h2>
        <button className="view-toggle" onClick={toggleView}>
          {view === 'grid' ? 'ðŸ“‹ Switch to List View' : 'ðŸ”³ Switch to Grid View'}
        </button>
      </div>

      {/* ðŸ”¥ Dynamic Class Here */}
      <div className={view === 'grid' ? 'mobile-grid' : 'mobile-list'}>
        {mobiles.map((mobile) => (
          <Link to={`/mobile/${mobile._id}`} key={mobile._id} className="mobile-card-link">
            <div className="mobile-card">
              <img src={mobile.imageUrl} alt={mobile.model} />
              <div className="card-text">
                <h4>{mobile.brand} - {mobile.model}</h4>
                <p>Click for details</p>
              </div>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
