import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './MobileDetailPage.css';

const MobileDetailPage = () => {
  const { id } = useParams();
  const [mobile, setMobile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchMobile = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/mobiles/${id}`);
      setMobile(res.data);
    } catch (err) {
      console.error('❌ Failed to fetch mobile:', err);
      setError('Mobile not found or server error.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchMobile();
  }, [id]);

  if (loading) return <div className="loading">Loading...</div>;
  if (error || !mobile) return <div className="error-message">{error || 'Mobile not found.'}</div>;

  return (
    <div className="mobile-detail-container">
      <div className="mobile-detail-card">
        <div className="mobile-images">
          {mobile.imageUrls?.map((url, idx) => (
            <img key={idx} src={url} alt={`Mobile ${idx}`} />
          ))}
        </div>

        <div className="mobile-info">
          <h2>{mobile.brand} {mobile.model}</h2>
          <p><strong>RAM:</strong> {mobile.ram}</p>
          <p><strong>Storage:</strong> {mobile.storage}</p>
          <p><strong>Color:</strong> {mobile.color}</p>
          <p><strong>Condition:</strong> {mobile.condition || 'N/A'}</p>
          <p className="price"><strong>Price:</strong> ₹{mobile.price}</p>
        </div>
      </div>
    </div>
  );
};

export default MobileDetailPage;
