import React, { useState } from 'react';
import axios from 'axios';

const HomePage = () => {
  const [mobiles, setMobiles] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/mobiles');
      const filtered = res.data.filter((m) =>
        m.model.toLowerCase().includes(searchTerm.toLowerCase()) ||
        m.brand.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setMobiles(filtered);
    } catch (err) {
      console.error('Failed to load catalog:', err);
    }
  };

  return (
    <div style={{ padding: '40px' }}>
      <h2>üì± Available Mobiles</h2>

      {/* üîç Search Input and Button */}
      <div style={{ marginBottom: '20px', display: 'flex', gap: '10px' }}>
        <input
          type="text"
          placeholder="Search by brand or model..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{
            flex: 1,
            padding: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />
        <button
          onClick={handleSearch}
          style={{
            padding: '10px 20px',
            border: 'none',
            backgroundColor: '#007bff',
            color: 'white',
            borderRadius: '6px',
            cursor: 'pointer'
          }}
        >
          üîç Search
        </button>
      </div>

      {/* üì¶ Mobile List */}
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
        {mobiles.map((mobile) => (
          <div
            key={mobile._id}
            style={{ border: '1px solid #ccc', padding: '10px', width: '220px' }}
          >
            <img src={mobile.imageUrl} alt={mobile.model} style={{ width: '100%' }} />
            <h4>{mobile.brand} - {mobile.model}</h4>
            <p>RAM: {mobile.ram}</p>
            <p>Storage: {mobile.storage}</p>
            <p>‚Çπ{mobile.price}</p>
            <button onClick={() => {
              const msg = `I'm interested in this mobile:\n${mobile.brand} ${mobile.model}`;
              const url = `https://wa.me/?text=${encodeURIComponent(msg)}`;
              window.open(url, '_blank');
            }}>üì© WhatsApp</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
