import React, { useContext, useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { CartContext } from '../context/CartContext';
import { SearchContext } from '../context/SearchContext';
import './Navbar.css';

const Navbar = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const isAdminPage = location.pathname.startsWith('/admin');
  const { cart } = useContext(CartContext);
  const { searchQuery, setSearchQuery } = useContext(SearchContext);
  const [menuOpen, setMenuOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  return (
    <nav className="navbar">
      <div className="navbar-top">
        <Link to="/" className="navbar-logo">JollyBaba ðŸ“±</Link>

        {!isAdminPage && (
          <input
            type="text"
            className="navbar-search"
            placeholder="Search mobiles..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        )}

        <button className="hamburger" onClick={() => setMenuOpen(!menuOpen)}>
          â˜°
        </button>
      </div>

      <div className={`navbar-actions ${menuOpen ? 'open' : ''}`}>
        {isAdminPage && token ? (
          <>
            <Link to="/admin">
              <button className="nav-btn">Upload</button>
            </Link>
            <Link to="/admin/manage">
              <button className="nav-btn">Manage</button>
            </Link>
            <button className="nav-btn logout" onClick={handleLogout}>
              Logout
            </button>
          </>
        ) : (
          <>
            <Link to="/cart" className="cart-button">
              ðŸ›’
              {cart.length > 0 && <span className="cart-count">{cart.length}</span>}
            </Link>
            <Link to="/login">
              <button className="nav-btn">Login as Admin</button>
            </Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
